mapper.select:
select KNOWLEDGE_QUESTION_NO, KNOWLEDGE_QUESTION.MEMBER_NO, m.MEMBER_NICKNAME, KNOWLEDGE_QUESTION_TITLE, KNOWLEDGE_QUESTION_CONTENT, KNOWLEDGE_QUESTION_READ_COUNT, KNOWLEDGE_QUESTION_WRITE_DATE, KNOWLEDGE_QUESTION_POINT
from KNOWLEDGE_QUESTION join MEMBER M on M.MEMBER_NO = KNOWLEDGE_QUESTION.MEMBER_NO
where KNOWLEDGE_QUESTION_NO = #{knowledgeQuestionNo}
