mapper.select:
select p.PRODUCT_CATEGORY_NO, p.PRODUCT_NO, p.PRODUCT_NAME, p.PRODUCT_PRICE,
       (select PRODUCT_IMAGE_FILENAME from PRODUCT_IMAGE pi where p.PRODUCT_NO = pi.PRODUCT_NO) as product_image,
       (select count(*) from FAVORITE where p.product_no=FAVORITE.product_no) as favorite_enabled
from PRODUCT p where p.PRODUCT_CATEGORY_NO=#{categoryNo}
order by p.product_no desc;
offset #{startRowNum} rows fetch next #{endRowNum} rows only
