mapper.select:
select * from
(
    select rownum as rnum, p.* from
    (
        select p.product_no, p.product_name, p.product_price,
        'http://localhost:8081/api/v1/image/product?name=' || pi.product_image_filename as product_image
        from PRODUCT p left join PRODUCT_IMAGE pi on
        p.product_no=pi.product_no and pi.product_image_no = 1
         where p.product_category_no=#{categoryNo}
        order by p.product_no desc
    ) p
    where rownum<=#{endRowNum}
)
where rnum>=#{startRowNum}

