mapper.select:
select * from
    (
        select rownum as rnum, a.* from
            (
                select
                  report_no,member_no,report_category,report_member_no,report_content,report_date
                from report
            ) a
        where rownum<=#{endRownum}
    )
where rnum>=#{startRownum}

select * from
    (
        select rownum as rnum, a.* from
            (
                select
                    report_no,member_no,report_category,report_member_no,report_content,report_date
                from report where member_no=#{memberNo}
            ) a
        where rownum<=#{endRownum}
    )
where rnum>=#{startRownum}
