mapper.select:
select * from
    (
        select rownum as rnum, r.* from
            (
                select
                    (select member_nickname from member where member_no= review.puching_review_writer) reviewWriter,puching_review_content as reviewContent
                from PUCHING_REVIEW review where puching_review_receiver=#{memberNo}
            ) r
        where rownum<=#{endRownum}
    )
where rnum>=#{startRownum}

select * from
    (
        select rownum as rnum, r.* from
            (
                select
                    puching_review_receiver,(select member_nickname from member where member_no= review.puching_review_receiver) reviewReceiver,puching_review_content as reviewContent
                from PUCHING_REVIEW review where puching_review_writer = #{memberNo}
            ) r
        where rownum<=#{endRownum}
    )
where rnum>=#{startRownum}
